fn main() {
    let st: &str = "";
    let size = 14; // Size of substring. Replace with 4 for part 1 or 14 for part 2

    let mut bool = false;
    let mut count = size - 1;

    // Scanning through the string in 4 character strings
    while count < st.len() && !bool {
        bool = true;
        for i in 0..size {
            for j in i + 1..size {
                if st.chars().nth(count - i) == st.chars().nth(count - j) {
                    bool = false;
                }
            }
        }
        count = count + 1;
    }

    if count != st.len() {
        println!("{}", count);
    } else {
        println!("FAIL");
    }
}
